import React, { createContext, useState, useContext, useEffect, useCallback } from 'react';
import { useConfig } from './ConfigContext';
import { useNotification } from './NotificationContext';

// 创建上下文
const WebSocketContext = createContext();

// WebSocket提供者组件
export const WebSocketProvider = ({ children }) => {
  const { config } = useConfig();
  const { success, error } = useNotification();

  const [socket, setSocket] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('offline');
  const [statusMessage, setStatusMessage] = useState('等待连接...');
  const [isLoading, setIsLoading] = useState(false);
  const [loadingMessage, setLoadingMessage] = useState('');

  // 更新连接状态
  const updateStatus = useCallback((state, text) => {
    setConnectionStatus(state);
    setStatusMessage(text);
  }, []);

  // 处理服务器消息
  const handleServerMessage = useCallback((message) => {
    // 此函数将由具体的组件实现，这里只是提供接口
    // 派发自定义事件，让相关组件可以捕获和处理
    const event = new CustomEvent('server-message', { detail: message });
    window.dispatchEvent(event);

    // 处理加载状态
    if (message.type === 'response' || message.type === 'error') {
      setIsLoading(false);
    }
  }, []);

  // 处理音频响应
  const handleAudioResponse = useCallback((audioBlob) => {
    // 此函数将由具体的组件实现，这里只是提供接口
    // 派发自定义事件，让相关组件可以捕获和处理
    const event = new CustomEvent('audio-response', { detail: audioBlob });
    window.dispatchEvent(event);

    // 处理加载状态
    setIsLoading(false);
  }, []);

  // 初始化WebSocket连接
  const initWebSocket = useCallback(() => {
    // 关闭已存在的连接
    if (socket) {
      socket.close();
      setSocket(null);
    }

    // 更新状态为正在连接
    updateStatus('connecting', '正在连接到服务器...');

    // 明确使用后端服务器的WebSocket地址(端口8000)
    const wsUrl = 'ws://localhost:8000/ws';

    let reconnectAttempts = 0;
    const maxReconnectAttempts = 5;
    const baseReconnectDelay = 1000; // 1秒

    const connect = () => {
      updateStatus('connecting', `连接中... ${reconnectAttempts > 0 ? `(尝试 ${reconnectAttempts}/${maxReconnectAttempts})` : ''}`);

      console.log(`尝试连接到WebSocket: ${wsUrl}`);

      // 创建WebSocket连接
      const newSocket = new WebSocket(wsUrl);

      // 连接打开
      newSocket.onopen = () => {
        console.log('WebSocket连接已建立');
        setIsConnected(true);
        updateStatus('online', '已连接');
        reconnectAttempts = 0; // 重置重连计数

        // 发送配置
        newSocket.send(JSON.stringify({
          type: 'config',
          config: config
        }));

        // 显示成功通知
        if (reconnectAttempts > 0) {
          success('连接已恢复');
        }
      };

      // 接收消息
      newSocket.onmessage = (event) => {
        // 处理二进制数据 (音频)
        if (event.data instanceof Blob) {
          handleAudioResponse(event.data);
        } 
        // 处理JSON数据
        else {
          try {
            const message = JSON.parse(event.data);
            handleServerMessage(message);
          } catch (e) {
            console.error('解析服务器消息失败:', e);
            error('服务器返回了无效数据');
          }
        }
      };

      // 连接关闭
      newSocket.onclose = (event) => {
        console.log(`WebSocket连接已关闭: ${event.code}`);
        setIsConnected(false);
        updateStatus('offline', '已断开连接');

        // 重连逻辑
        if (reconnectAttempts < maxReconnectAttempts) {
          const delay = baseReconnectDelay * Math.pow(1.5, reconnectAttempts);
          reconnectAttempts++;
          console.log(`尝试重新连接... ${reconnectAttempts}/${maxReconnectAttempts} (${delay}ms后)`);

          updateStatus('connecting', `将在${Math.round(delay/1000)}秒后重新连接...`);

          setTimeout(connect, delay);
        } else {
          updateStatus('offline', '连接失败，请刷新页面');
          error('无法连接到服务器，请刷新页面重试');
        }
      };

      // 连接错误
      newSocket.onerror = (connectionError) => {
        console.error('WebSocket错误:', connectionError);
        updateStatus('offline', '连接错误');
      };

      setSocket(newSocket);
    };

    // 初始连接
    connect();

    // 清理函数
    return () => {
      if (socket) {
        socket.close();
      }
    };
  }, [config, error, success, updateStatus, handleServerMessage, handleAudioResponse, socket]);

  // 发送消息
  const sendMessage = useCallback((messageType, data) => {
    if (!socket || !isConnected) {
      error('未连接到服务器，请等待连接恢复');
      return false;
    }

    const message = {
      type: messageType,
      ...data
    };

    socket.send(JSON.stringify(message));
    return true;
  }, [socket, isConnected, error]);

  // 发送音频数据
  const sendAudioData = useCallback((audioBlob) => {
    if (!socket || !isConnected) {
      error('未连接到服务器，请等待连接恢复');
      return false;
    }

    socket.send(audioBlob);
    return true;
  }, [socket, isConnected, error]);

  // 设置加载状态
  const setLoading = useCallback((loading, message = '处理中...') => {
    setIsLoading(loading);
    setLoadingMessage(message);
  }, []);

  // 初始化WebSocket
  useEffect(() => {
    initWebSocket();
  }, [initWebSocket]);

  return (
    <WebSocketContext.Provider
      value={{
        socket,
        isConnected,
        connectionStatus,
        statusMessage,
        isLoading,
        loadingMessage,
        setLoading,
        sendMessage,
        sendAudioData
      }}
    >
      {children}
    </WebSocketContext.Provider>
  );
};

// 自定义Hook，用于在组件中访问WebSocket功能
export const useWebSocket = () => {
  const context = useContext(WebSocketContext);
  if (!context) {
    throw new Error('useWebSocket must be used within a WebSocketProvider');
  }
  return context;
};
