import React, { useState, useEffect, useRef } from 'react';
import './ChatSection.css';
import { useWebSocket } from '../../contexts/WebSocketContext';
import { useNotification } from '../../contexts/NotificationContext';
import { useConfig } from '../../contexts/ConfigContext';

const ChatSection = () => {
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [isGeneratingVideo, setIsGeneratingVideo] = useState(false);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);
  const { sendMessage, sendAudioData, isConnected, setLoading } = useWebSocket();
  const { error, success } = useNotification();
  const { config } = useConfig();

  // 媒体录制相关的状态
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [audioChunks, setAudioChunks] = useState([]);

  // 音频配置
  const audioConfig = {
    sampleRate: 16000,
    channels: 1
  };

  // 监听服务器消息
  useEffect(() => {
    const handleServerMessage = (event) => {
      const message = event.detail;

      switch (message.type) {
        case 'transcription':
          if (message.text) {
            addMessage(message.text, 'user');
          }
          break;

        case 'response':
          if (message.text) {
            addMessage(message.text, 'ai');
          }
          break;

        case 'error':
          addStatusMessage(`错误: ${message.message}`);
          break;

        default:
          break;
      }
    };

    window.addEventListener('server-message', handleServerMessage);

    // 清理监听器
    return () => {
      window.removeEventListener('server-message', handleServerMessage);
    };
  }, []);

  // 监听音频响应
  useEffect(() => {
    const handleAudioResponse = (event) => {
      const audioBlob = event.detail;

      // 创建音频URL并播放
      const audioUrl = URL.createObjectURL(audioBlob);
      const audio = new Audio(audioUrl);

      // 播放结束时释放资源
      audio.onended = () => {
        URL.revokeObjectURL(audioUrl);
      };

      // 播放音频
      audio.play().catch(err => {
        console.error('播放音频失败:', err);
      });
    };

    window.addEventListener('audio-response', handleAudioResponse);

    // 清理监听器
    return () => {
      window.removeEventListener('audio-response', handleAudioResponse);
    };
  }, []);

  // 自动滚动到最新消息
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // 添加消息到聊天记录
  const addMessage = (text, sender) => {
    const time = getFormattedTime();
    setMessages(prevMessages => [
      ...prevMessages,
      { text, sender, time }
    ]);
  };

  // 添加状态消息
  const addStatusMessage = (text) => {
    const time = getFormattedTime();
    setMessages(prevMessages => [
      ...prevMessages,
      { text, sender: 'status', time }
    ]);
  };

  // 获取格式化的时间
  const getFormattedTime = () => {
    const now = new Date();
    return `${now.getHours().toString().padStart(2, '0')}:${now.getMinutes().toString().padStart(2, '0')}`;
  };

  // 滚动到底部
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  // 发送文本消息
  const handleSendMessage = async () => {
    const text = inputText.trim();

    if (text && isConnected) {
      // 添加消息到聊天窗口
      addMessage(text, 'user');

      // 清空输入框
      setInputText('');

      if (config.useEchoMimic && config.refImagePath) {
        // EchoMimic模式 - 直接生成视频
        try {
          setIsGeneratingVideo(true);
          setLoading(true, '生成视频中...');
          addStatusMessage('正在生成视频响应...');

          const response = await fetch('/api/text_to_video', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify({ text })
          });

          if (!response.ok) {
            throw new Error('视频生成请求失败');
          }

          const result = await response.json();

          if (result.success) {
            // 视频生成成功，触发视频可用事件
            const videoEvent = new CustomEvent('videoAvailable', {
              detail: { videoUrl: result.videoUrl }
            });
            window.dispatchEvent(videoEvent);

            // 添加AI响应到聊天记录
            if (result.text) {
              addMessage(result.text, 'ai');
            }

            addStatusMessage('视频生成成功');
          } else {
            throw new Error(result.error || '视频生成失败');
          }

        } catch (err) {
          console.error('视频生成错误:', err);
          error(`视频生成失败: ${err.message}`);
          addStatusMessage(`错误: ${err.message}`);

          // 尝试回退到普通文本模式
          sendMessage('text_input', { text });
        } finally {
          setLoading(false);
          setIsGeneratingVideo(false);
        }
      } else {
        // 普通模式 - 使用现有流程
        setLoading(true, '处理中...');
        sendMessage('text_input', { text });
      }
    } else if (!isConnected) {
      error('未连接到服务器，请等待连接恢复');
    }
  };

  // 处理键盘事件
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  // 切换录音状态
  const toggleRecording = async () => {
    if (isRecording) {
      stopRecording();
    } else {
      await startRecording();
    }
  };

  // 开始录音
  const startRecording = async () => {
    if (isRecording || !isConnected) {
      if (!isConnected) {
        error('未连接到服务器，请等待连接恢复');
      }
      return;
    }

    try {
      // 请求麦克风访问权限
      const stream = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          sampleRate: audioConfig.sampleRate,
          channelCount: audioConfig.channels,
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true
        } 
      });

      // 创建MediaRecorder实例
      const recorder = new MediaRecorder(stream);
      setMediaRecorder(recorder);
      setAudioChunks([]);

      // 收集音频数据
      recorder.addEventListener('dataavailable', event => {
        if (event.data.size > 0) {
          setAudioChunks(prev => [...prev, event.data]);
        }
      });

      // 录音结束后发送数据
      recorder.addEventListener('stop', async () => {
        const chunks = audioChunks;
        if (chunks.length > 0 && isConnected) {
          const audioBlob = new Blob(chunks, { type: 'audio/wav' });

          if (config.useEchoMimic && config.refImagePath) {
            // EchoMimic模式 - 生成视频
            try {
              setIsGeneratingVideo(true);
              setLoading(true, '生成视频中...');
              addStatusMessage('正在处理语音并生成视频...');

              // 创建 FormData 对象
              const formData = new FormData();
              formData.append('audio', audioBlob, 'audio.wav');

              // 发送生成视频请求
              const response = await fetch('/api/generate_video', {
                method: 'POST',
                body: formData
              });

              if (!response.ok) {
                throw new Error('视频生成请求失败');
              }

              const result = await response.json();

              if (result.success) {
                // 视频生成成功，触发视频可用事件
                const videoEvent = new CustomEvent('videoAvailable', {
                  detail: { videoUrl: result.videoUrl }
                });
                window.dispatchEvent(videoEvent);

                // 添加转录文本到聊天记录
                if (result.transcription) {
                  addMessage(result.transcription, 'user');
                }

                // 添加AI响应到聊天记录
                if (result.response) {
                  addMessage(result.response, 'ai');
                }

                addStatusMessage('视频生成成功');
              } else {
                throw new Error(result.error || '视频生成失败');
              }

            } catch (err) {
              console.error('视频生成错误:', err);
              error(`视频生成失败: ${err.message}`);
              addStatusMessage(`错误: ${err.message}`);

              // 尝试回退到普通语音模式
              sendAudioData(audioBlob);
            } finally {
              setLoading(false);
              setIsGeneratingVideo(false);
            }
          } else {
            // 普通模式 - 使用现有流程
            setLoading(true, '处理语音中...');
            addStatusMessage('正在处理语音...');
            sendAudioData(audioBlob);
          }
        }
      });

      // 开始录音
      recorder.start(100); // 每100ms触发一次dataavailable事件

      // 更新UI
      setIsRecording(true);

    } catch (err) {
      console.error('启动录音失败:', err);
      error('无法访问麦克风，请确保已授予权限');
    }
  };

  // 停止录音
  const stopRecording = () => {
    if (!isRecording || !mediaRecorder) return;

    // 停止MediaRecorder
    mediaRecorder.stop();

    // 停止所有音轨
    mediaRecorder.stream.getTracks().forEach(track => track.stop());

    // 更新UI
    setIsRecording(false);
  };

  return (
    <div className="chat-section">
      <div className="chat-title">对话</div>

      <div className="chat-messages">
        {messages.length === 0 && (
          <div className="empty-chat">
            <i className="fas fa-comments"></i>
            <p>聊天已准备好，发送消息开始对话吧</p>
          </div>
        )}

        {messages.map((message, index) => (
          <div 
            key={index} 
            className={`message message-${message.sender}`}
          >
            {message.sender === 'status' ? (
              <div className="status-message">{message.text}</div>
            ) : (
              <>
                <div className="message-text">{message.text}</div>
                <div className="message-time">{message.time}</div>
              </>
            )}
          </div>
        ))}

        <div ref={messagesEndRef} />
      </div>

      <div className="chat-input">
        <textarea
          ref={inputRef}
          className="user-input"
          placeholder="输入消息..."
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          onKeyPress={handleKeyPress}
        />

        <div className="input-controls">
          <button 
            className={`record-btn ${isRecording ? 'recording' : ''}`}
            onClick={toggleRecording}
            title={isRecording ? '停止录音' : '开始录音'}
          >
            <i className={`fas ${isRecording ? 'fa-stop' : 'fa-microphone'}`}></i>
            <span className="record-text">
              {isRecording ? '停止录音' : '开始录音'}
            </span>
          </button>

          <button 
            className="send-btn"
            onClick={handleSendMessage}
            disabled={!inputText.trim()}
            title="发送消息"
          >
            <i className="fas fa-paper-plane"></i>
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatSection;
