import React, { useEffect, useRef, useState } from 'react';
import './DigitalHuman.css';
import { useConfig } from '../../contexts/ConfigContext';
import { useNotification } from '../../contexts/NotificationContext';

const DigitalHuman = ({ modelPath }) => {
  const canvasRef = useRef(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [model, setModel] = useState(null);
  const { config } = useConfig();
  const { success, error: showError } = useNotification();

  // Live2D模型初始化
  useEffect(() => {
    let mounted = true;
    let app = null;

    // 如果使用EchoMimic，不加载Live2D模型
    if (config.useEchoMimic) {
      console.log('已启用EchoMimic，跳过Live2D模型加载');
      setLoading(false);
      setError(null);
      return () => {};
    }

    const initLive2D = async () => {
      console.log('初始化Live2D模型');

      try {
        // 确保所有必要的脚本已加载
        // 如果没有加载，我们需要手动加载
        if (!window.PIXI) {
          console.log('PIXI未加载，尝试手动加载');
          await loadScript('https://cdn.jsdelivr.net/npm/pixi.js@6.5.2/dist/browser/pixi.min.js');
          await loadScript('https://cdn.jsdelivr.net/npm/@pixi/math-extras@6.5.2/dist/math-extras.min.js');
        }

        if (!window.Live2DCubismCore) {
          console.log('Live2DCubismCore未加载，尝试手动加载');
          await loadScript('https://cubism.live2d.com/sdk-web/cubismcore/live2dcubismcore.min.js');
        }

        if (!window.PIXI || !window.PIXI.live2d) {
          console.log('PIXI.live2d未加载，尝试手动加载');
          await loadScript('https://cdn.jsdelivr.net/npm/pixi-live2d-display@0.4.0/dist/index.min.js');
        }

        // 最终检查所有库是否已正确加载
        if (!window.PIXI) {
          throw new Error('PIXI未加载，请检查网络连接并刷新页面');
        }

        if (!window.PIXI.live2d || !window.PIXI.live2d.Live2DModel) {
          throw new Error('PIXI.live2d.Live2DModel未加载，请检查网络连接并刷新页面');
        }

        console.log('所有库已加载，开始初始化模型');

        setLoading(true);
        setError(null);

        const canvas = canvasRef.current;
        if (!canvas) {
          throw new Error('Canvas元素不存在');
        }

        console.log('创建PIXI应用');

        // 创建PIXI应用
        app = new window.PIXI.Application({
          view: canvas,
          autoStart: true,
          backgroundAlpha: 0,
          backgroundColor: 0xffffff,
          resizeTo: canvas.parentElement
        });

        // 设置模型缩放模式
        if (typeof window.PIXI.live2d.Live2DModel.registerTicker === 'function') {
          console.log('注册PIXI ticker');
          window.PIXI.live2d.Live2DModel.registerTicker(app.ticker);
        } else {
          console.warn('registerTicker方法未找到，可能使用不同版本的库');
        }

        // 构建完整的模型路径
        const fullModelPath = modelPath.startsWith('/') 
          ? `${window.location.origin}${modelPath}` 
          : `${window.location.origin}/${modelPath}`;

        console.log(`开始加载Live2D模型: ${fullModelPath}`);

        try {
          const live2dModel = await window.PIXI.live2d.Live2DModel.from(fullModelPath);

          if (!live2dModel) {
            throw new Error('模型加载失败，请检查模型文件是否存在');
          }

          console.log('模型加载成功，进行缩放和定位');

          // 调整模型位置和缩放
          const scaleX = (canvas.width * 0.8) / live2dModel.width;
          const scaleY = (canvas.height * 0.8) / live2dModel.height;
          const scale = Math.min(scaleX, scaleY);

          live2dModel.scale.set(scale);
          live2dModel.anchor.set(0.5, 0.5);
          live2dModel.position.set(canvas.width / 2, canvas.height / 2);

          // 创建容器并添加模型
          const container = new window.PIXI.Container();
          container.addChild(live2dModel);
          app.stage.addChild(container);

          // 监听窗口大小变化
          const resizeHandler = () => {
            if (live2dModel) {
              // 调整画布尺寸
              app.renderer.resize(canvas.parentElement.clientWidth, canvas.parentElement.clientHeight);

              // 重新定位模型
              live2dModel.position.set(
                app.renderer.width / 2,
                app.renderer.height / 2
              );
            }
          };

          window.addEventListener('resize', resizeHandler);

          if (mounted) {
            setModel(live2dModel);
            setLoading(false);
            success('Live2D模型加载成功');
          }
        } catch (modelError) {
          console.error('加载模型文件失败:', modelError);
          throw new Error(`加载模型文件失败: ${modelError.message || '请检查模型路径是否正确'}`);
        }

      } catch (err) {
        console.error('加载Live2D模型失败:', err);
        if (mounted) {
          setError(err.message || '模型加载失败，请检查路径是否正确');
          setLoading(false);
          showError('Live2D模型加载失败: ' + (err.message || '请检查路径是否正确'));
        }
      }
    };

    // 延迟一点初始化，确保DOM和脚本已完全加载
    setTimeout(() => {
      initLive2D();
    }, 1000);

    return () => {
      mounted = false;
      if (app) {
        app.destroy(true, {children: true, texture: true, baseTexture: true});
      }
    };
  }, [modelPath, success, showError, config.useEchoMimic]);

  // 处理服务器动画消息
  useEffect(() => {
    const handleServerMessage = (event) => {
      const message = event.detail;

      // 只有当不使用EchoMimic时才处理动画
      if (message.type === 'response' && !message.useEchoMimic && message.animation && model) {
        playAnimation(model, message.animation);
      }
    };

    window.addEventListener('server-message', handleServerMessage);

    return () => {
      window.removeEventListener('server-message', handleServerMessage);
    };
  }, [model]);

  // 播放动画
  const playAnimation = (model, animation) => {
    try {
      // 播放表情
      if (animation.expression) {
        const expressionIndex = model.internalModel.motionManager.expressionManager?.expressionNames.findIndex(
          name => name.toLowerCase().includes(animation.expression)
        );

        if (expressionIndex !== -1 && expressionIndex !== undefined) {
          model.internalModel.motionManager.expressionManager?.setExpression(expressionIndex);
        }
      }

      // 播放动作
      if (animation.motion) {
        playMotion(model, animation.motion, animation.intensity || 0.8);
      }
    } catch (error) {
      console.error('播放动画失败:', error);
    }
  };

  // 播放动作
  const playMotion = (model, motionName, intensity = 0.8) => {
    try {
      let motionGroup = 'Idle';

      switch (motionName.toLowerCase()) {
        case 'talking':
          motionGroup = 'Talk';
          break;
        case 'nodding':
          motionGroup = 'Nod';
          break;
        case 'thinking':
          motionGroup = 'Think';
          break;
        case 'greeting':
          motionGroup = 'Greeting';
          break;
        case 'surprised':
          motionGroup = 'Surprised';
          break;
        default:
          motionGroup = 'Idle';
      }

      // 查找模型中是否有对应的动作组
      const groups = model.internalModel.motionManager.definitions;
      const availableGroups = Object.keys(groups);

      // 如果没有完全匹配的组，尝试部分匹配
      const bestMatch = availableGroups.find(group => 
        group.toLowerCase().includes(motionGroup.toLowerCase())
      ) || availableGroups[0];

      if (bestMatch) {
        const availableMotions = groups[bestMatch].length;
        const motionIndex = Math.floor(Math.random() * availableMotions);
        model.motion(bestMatch, motionIndex, intensity);
      }
    } catch (error) {
      console.error('播放动作失败:', error);
    }
  };

  // 加载脚本工具函数
  const loadScript = (src) => {
    return new Promise((resolve, reject) => {
      console.log(`尝试加载脚本: ${src}`);
      const script = document.createElement('script');
      script.src = src;
      script.async = true;
      script.onload = () => {
        console.log(`脚本加载成功: ${src}`);
        resolve();
      };
      script.onerror = (err) => {
        console.error(`脚本加载失败: ${src}`, err);
        reject(new Error(`脚本加载失败: ${src}`));
      };
      document.head.appendChild(script);
    });
  };

  return (
    <div className="digital-human-section">
      <div className="model-container">
        <canvas ref={canvasRef} id="live2d-canvas"></canvas>

        {loading && (
          <div className="model-placeholder">
            <i className="fas fa-spinner fa-spin"></i>
            <p>Live2D 模型加载中...</p>
          </div>
        )}

        {error && !loading && (
          <div className="model-placeholder error">
            <i className="fas fa-exclamation-triangle"></i>
            <p>Live2D模型加载失败</p>
            <p className="error-message">{error}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default DigitalHuman;
