import React, { useState, useEffect } from 'react';
import './App.css';

// 引入上下文
import { useWebSocket } from './contexts/WebSocketContext';
import { useConfig } from './contexts/ConfigContext';
import { useNotification } from './contexts/NotificationContext';

// 创建一个非常简单的应用组件来测试React渲染
function App() {
  const [count, setCount] = useState(0);
  const { connectionStatus, statusMessage, sendMessage } = useWebSocket();
  const { config } = useConfig();
  const { addNotification } = useNotification();

  // 测试通知功能
  const testNotification = () => {
    addNotification('测试通知', 'info');
  };

  // 测试WebSocket功能
  const testWebSocket = () => {
    sendMessage({ type: 'ping', data: { timestamp: Date.now() } });
  };

  return (
    <div className="test-app">
      <h1>React渲染测试</h1>
      <p>如果你能看到这个页面，说明React正在正常渲染</p>

      <div className="counter">
        <p>计数器: {count}</p>
        <button onClick={() => setCount(count + 1)}>
          增加
        </button>
      </div>

      <div className="connection-status">
        <h2>WebSocket连接状态</h2>
        <p>状态: {connectionStatus}</p>
        <p>消息: {statusMessage}</p>
        <button onClick={testWebSocket}>
          测试WebSocket
        </button>
      </div>

      <div className="config-info">
        <h2>配置信息</h2>
        <pre>{JSON.stringify(config, null, 2)}</pre>
      </div>

      <div className="notification-test">
        <h2>通知测试</h2>
        <button onClick={testNotification}>
          显示测试通知
        </button>
      </div>
    </div>
  );
}

export default App;
