import React, { useState, useEffect, useRef } from 'react';
import './ChatSection.css';
import { useWebSocket } from '../../contexts/WebSocketContext';
import { useNotification } from '../../contexts/NotificationContext';
import { useConfig } from '../../contexts/ConfigContext';

const ChatSection = () => {
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [isGeneratingVideo, setIsGeneratingVideo] = useState(false);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);
  const { sendMessage, sendAudioData, isConnected, setLoading } = useWebSocket();
  const { error, success } = useNotification();
  const { config } = useConfig();

  // Â™í‰ΩìÂΩïÂà∂Áõ∏ÂÖ≥ÁöÑÁä∂ÊÄÅ
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [audioChunks, setAudioChunks] = useState([]);

  // Èü≥È¢ëÈÖçÁΩÆ
  const audioConfig = {
    sampleRate: 16000,
    channels: 1
  };

  // ÁõëÂê¨ÊúçÂä°Âô®Ê∂àÊÅØ
  useEffect(() => {
    const handleServerMessage = (event) => {
      const message = event.detail;
      console.log('ChatSectionÊî∂Âà∞ÊúçÂä°Âô®Ê∂àÊÅØ:', message);

      // Â§ÑÁêÜËÅäÂ§©Ê∂àÊÅØ
      if (message.type === 'response') {
        // Ê∑ªÂä†Âä©ÊâãÂõûÂ§ç
        if (message.text) {
          addMessage('assistant', message.text);
        }

        // Ê∑ªÂä†Áä∂ÊÄÅÊ∂àÊÅØ
        if (message.status && message.message) {
          addStatusMessage(message.message);
        }

        // Â§ÑÁêÜÂèÇËÄÉÂõæÂÉè
        if (message.refImagePath) {
          addMessage('system', `ÂèÇËÄÉÂõæÂÉè: ${config.refImagePath}`);
        }
      }
      // Â§ÑÁêÜÈîôËØØÊ∂àÊÅØ
      else if (message.type === 'error') {
        error(message.message || 'Âá∫Áé∞ÈîôËØØÔºåËØ∑ÈáçËØï');
        addStatusMessage('ÈîôËØØ: ' + (message.message || 'Êú™Áü•ÈîôËØØ'));
      }
    };

    window.addEventListener('server-message', handleServerMessage);

    return () => {
      window.removeEventListener('server-message', handleServerMessage);
    };
  }, []);

  // ÂàùÂßãÂåñÂ™í‰ΩìÂΩïÂà∂
  const initMediaRecorder = async () => {
    try {
      console.log('ÂàùÂßãÂåñÂ™í‰ΩìÂΩïÂà∂Âô®');

      // ËØ∑Ê±ÇÈü≥È¢ëÊùÉÈôê
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });

      // ËÆæÁΩÆÈü≥È¢ëÂ§ÑÁêÜ
      const options = { mimeType: 'audio/webm' };
      const recorder = new MediaRecorder(stream, options);

      // Êî∂ÈõÜÈü≥È¢ëÊï∞ÊçÆ
      recorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          setAudioChunks(prev => [...prev, event.data]);
        }
      };

      // Â§ÑÁêÜÂΩïÂà∂ÂÅúÊ≠¢
      recorder.onstop = async () => {
        if (audioChunks.length === 0) {
          console.warn('Ê≤°ÊúâÊî∂ÈõÜÂà∞Èü≥È¢ëÊï∞ÊçÆ');
          return;
        }

        try {
          // ÂêàÂπ∂Èü≥È¢ëÂùó
          const audioBlob = new Blob(audioChunks, { type: 'audio/webm' });

          // ÂèëÈÄÅÈü≥È¢ëÊï∞ÊçÆÂà∞ÊúçÂä°Âô®
          console.log('ÂèëÈÄÅÈü≥È¢ëÊï∞ÊçÆ, Â§ßÂ∞è:', audioBlob.size);
          const result = sendAudioData(audioBlob, {
            language: config.language,
            voiceName: config.voiceName,
            useEchoMimic: config.useEchoMimic
          });

          if (result) {
            // Ê∑ªÂä†Áî®Êà∑Ê∂àÊÅØ (‰ΩøÁî®"ÂΩïÈü≥‰∏≠..."‰Ωú‰∏∫Âç†‰ΩçÁ¨¶)
            addMessage('user', 'üé§ ËØ≠Èü≥ËæìÂÖ•');

            // ÈáçÁΩÆÈü≥È¢ëÂùó
            setAudioChunks([]);

            // ËÆæÁΩÆÂä†ËΩΩÁä∂ÊÄÅ
            setLoading(true, 'Â§ÑÁêÜ‰∏≠...');
          }
        } catch (err) {
          console.error('Â§ÑÁêÜÂΩïÈü≥Â§±Ë¥•:', err);
          error('Â§ÑÁêÜÂΩïÈü≥Â§±Ë¥•');
        }
      };

      // ‰øùÂ≠òÂΩïÂà∂Âô®ÂÆû‰æã
      setMediaRecorder(recorder);
      success('È∫¶ÂÖãÈ£éÂàùÂßãÂåñÊàêÂäü');

    } catch (err) {
      console.error('ÂàùÂßãÂåñÂ™í‰ΩìÂΩïÂà∂Â§±Ë¥•:', err);
      error('Êó†Ê≥ïËÆøÈóÆÈ∫¶ÂÖãÈ£éÔºåËØ∑Á°Æ‰øùÂ∑≤Êéà‰∫àÊùÉÈôê');
    }
  };

  // ÁªÑ‰ª∂ÊåÇËΩΩÊó∂ÂàùÂßãÂåñÂ™í‰ΩìÂΩïÂà∂
  useEffect(() => {
    // Ëá™Âä®ÂàùÂßãÂåñÂ™í‰ΩìÂΩïÂà∂Âô®
    if (!mediaRecorder) {
      initMediaRecorder();
    }

    // ÁªÑ‰ª∂Âç∏ËΩΩÊó∂Ê∏ÖÁêÜ
    return () => {
      if (mediaRecorder && mediaRecorder.state !== 'inactive') {
        mediaRecorder.stop();
      }
    };
  }, []);

  // Ê∑ªÂä†Ê∂àÊÅØÂà∞ËÅäÂ§©
  const addMessage = (role, content) => {
    setMessages(prev => [
      ...prev, 
      { 
        id: Date.now(),
        role,
        content,
        timestamp: new Date().toLocaleTimeString()
      }
    ]);
  };

  // Ê∑ªÂä†Áä∂ÊÄÅÊ∂àÊÅØ
  const addStatusMessage = (content) => {
    setMessages(prev => [
      ...prev, 
      { 
        id: Date.now(),
        role: 'status',
        content,
        timestamp: new Date().toLocaleTimeString()
      }
    ]);
  };

  // Ëá™Âä®ÊªöÂä®Âà∞ÊúÄÊñ∞Ê∂àÊÅØ
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  // Â§ÑÁêÜËæìÂÖ•ÂèòÂåñ
  const handleInputChange = (e) => {
    setInputText(e.target.value);
  };

  // ÂºÄÂßãÂΩïÈü≥
  const startRecording = () => {
    if (!mediaRecorder) {
      initMediaRecorder().then(() => {
        if (mediaRecorder) {
          mediaRecorder.start();
          setIsRecording(true);
        }
      });
    } else {
      mediaRecorder.start();
      setIsRecording(true);
    }
  };

  // ÂÅúÊ≠¢ÂΩïÈü≥
  const stopRecording = () => {
    if (mediaRecorder && mediaRecorder.state !== 'inactive') {
      mediaRecorder.stop();
    }
    setIsRecording(false);
  };

  // ÂèëÈÄÅÊñáÊú¨Ê∂àÊÅØ
  const sendTextMessage = () => {
    if (!inputText.trim()) return;

    // Â¶ÇÊûúÊú™ËøûÊé•ÔºåÊòæÁ§∫ÈîôËØØ
    if (!isConnected) {
      error('Êú™ËøûÊé•Âà∞ÊúçÂä°Âô®ÔºåËØ∑Á≠âÂæÖËøûÊé•ÊÅ¢Â§ç');
      return;
    }

    // Â∞ÜÊ∂àÊÅØÊ∑ªÂä†Âà∞ËÅäÂ§©
    addMessage('user', inputText);

    // ÂèëÈÄÅÊ∂àÊÅØÂà∞ÊúçÂä°Âô®
    const result = sendMessage({
      type: 'message',
      text: inputText,
      language: config.language,
      voiceName: config.voiceName,
      useEchoMimic: config.useEchoMimic
    });

    // Â¶ÇÊûúÊàêÂäüÂèëÈÄÅÔºåÊ∏ÖÁ©∫ËæìÂÖ•Ê°ÜÂπ∂ËÆæÁΩÆÂä†ËΩΩÁä∂ÊÄÅ
    if (result) {
      setInputText('');
      setLoading(true, 'ÊÄùËÄÉ‰∏≠...');
    }
  };

  // ÁõëÂê¨ÂõûËΩ¶ÈîÆ
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendTextMessage();
    }
  };

  // Ê∏≤ÊüìÊ∂àÊÅØ
  const renderMessages = () => {
    return messages.map(message => (
      <div key={message.id} className={`message ${message.role}`}>
        {message.role === 'user' && <div className="avatar user-avatar"><i className="fas fa-user"></i></div>}
        {message.role === 'assistant' && <div className="avatar assistant-avatar"><i className="fas fa-robot"></i></div>}

        <div className="message-content">
          <div className="message-text">{message.content}</div>
          <div className="message-time">{message.timestamp}</div>
        </div>
      </div>
    ));
  };

  return (
    <div className="chat-section">
      <div className="chat-header">
        <h3>ËÅäÂ§©Á™óÂè£</h3>
        <div className="connection-status">
          <span className={`status-dot ${isConnected ? 'connected' : 'disconnected'}`}></span>
          <span className="status-text">{isConnected ? 'Â∑≤ËøûÊé•' : 'Êú™ËøûÊé•'}</span>
        </div>
      </div>

      <div className="chat-messages">
        {messages.length === 0 ? (
          <div className="empty-chat">
            <i className="fas fa-comment-dots"></i>
            <p>‰∏éÊï∞Â≠ó‰∫∫Âä©ÊâãÂºÄÂßãÂØπËØùÂêßÔºÅ</p>
            <p className="tip">‰Ω†ÂèØ‰ª•ËæìÂÖ•ÊñáÂ≠óÔºåÊàñÁÇπÂáªÈ∫¶ÂÖãÈ£éÊåâÈíÆËøõË°åËØ≠Èü≥ËæìÂÖ•</p>
          </div>
        ) : (
          renderMessages()
        )}
        <div ref={messagesEndRef} />
      </div>

      <div className="chat-input">
        <textarea
          ref={inputRef}
          value={inputText}
          onChange={handleInputChange}
          onKeyPress={handleKeyPress}
          placeholder="ËæìÂÖ•Ê∂àÊÅØ..."
          disabled={!isConnected}
        />

        <div className="input-controls">
          <button 
            className={`voice-btn ${isRecording ? 'recording' : ''}`}
            onClick={isRecording ? stopRecording : startRecording}
            disabled={!isConnected}
          >
            <i className={`fas ${isRecording ? 'fa-stop' : 'fa-microphone'}`}></i>
          </button>

          <button 
            className="send-btn" 
            onClick={sendTextMessage}
            disabled={!inputText.trim() || !isConnected}
          >
            <i className="fas fa-paper-plane"></i>
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatSection;
