import React, { useState, useEffect, useRef } from 'react';
import './SettingsPanel.css';
import { useConfig } from '../../contexts/ConfigContext';
import { useWebSocket } from '../../contexts/WebSocketContext';
import { useNotification } from '../../contexts/NotificationContext';

const SettingsPanel = ({ isOpen, onClose }) => {
  const { config, updateConfig } = useConfig();
  const { sendMessage, isConnected } = useWebSocket();
  const { success, error } = useNotification();

  // 本地表单状态
  const [formState, setFormState] = useState(config);
  const [voiceOptions, setVoiceOptions] = useState([]);
  const [isUploading, setIsUploading] = useState(false);
  const [presetImages, setPresetImages] = useState([]);
  const [selectedGender, setSelectedGender] = useState('woman'); // 默认选择女性图像
  const fileInputRef = useRef(null);

  // 加载预设图像
  useEffect(() => {
    const fetchPresetImages = async () => {
      try {
        setIsLoading(true);
        const gender = selectedGender; // 'man' 或 'woman'
        const response = await fetch(`http://localhost:8000/api/preset_images?gender=${gender}`);
        if (!response.ok) {
          throw new Error(`服务器返回错误: ${response.status}`);
        }
        const data = await response.json();
        setPresetImages(data.images || []);
      } catch (err) {
        console.error('获取预设图像错误:', err);
        error('获取预设图像失败: ' + err.message);
      } finally {
        setIsLoading(false);
      }
    };

    fetchPresetImages();
  }, [selectedGender]);

  // 根据语言更新语音选项
  useEffect(() => {
    const updateVoiceOptions = () => {
      if (formState.language === 'zh-CN') {
        setVoiceOptions([
          { value: 'aura-mandarin', label: 'Aura (女声)' },
          { value: 'nova-mandarin', label: 'Nova (女声)' },
          { value: 'shimmer-mandarin', label: 'Shimmer (女声)' },
          { value: 'storm-mandarin', label: 'Storm (男声)' },
          { value: 'warp-mandarin', label: 'Warp (男声)' }
        ]);
      } else if (formState.language === 'en-US') {
        setVoiceOptions([
          { value: 'aura', label: 'Aura (女声)' },
          { value: 'nova', label: 'Nova (女声)' },
          { value: 'shimmer', label: 'Shimmer (女声)' },
          { value: 'storm', label: 'Storm (男声)' },
          { value: 'warp', label: 'Warp (男声)' }
        ]);
      } else if (formState.language === 'ja-JP') {
        setVoiceOptions([
          { value: 'aura-japanese', label: 'Aura (女声)' },
          { value: 'nova-japanese', label: 'Nova (女声)' },
          { value: 'storm-japanese', label: 'Storm (男声)' }
        ]);
      } else {
        setVoiceOptions([]);
      }
    };

    updateVoiceOptions();

    // 检查当前选择的声音在新的选项中是否存在
    const voiceExists = voiceOptions.some(option => option.value === formState.voice);
    if (!voiceExists && voiceOptions.length > 0) {
      setFormState(prev => ({
        ...prev,
        voice: voiceOptions[0].value
      }));
    }
  }, [formState.language]);

  // 处理参考图像上传
  const handleRefImageUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setIsUploading(true);

    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await fetch('/api/upload_reference_image', {
        method: 'POST',
        body: formData
      });

      const result = await response.json();

      if (result.success) {
        // 更新配置
        updateConfig({
          refImagePath: result.file_path
        });
        success('参考图像上传成功');
      } else {
        error('上传失败: ' + (result.error || '未知错误'));
      }
    } catch (err) {
      console.error('上传参考图像错误:', err);
      error('上传失败: ' + (err.message || '未知错误'));
    } finally {
      setIsUploading(false);
    }
  };

  // 处理预设图像选择
  const handlePresetImageSelect = async (imagePath) => {
    try {
      // 向后端发送请求，使用预设图像
      const response = await fetch('/api/use_preset_image', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          image_path: imagePath
        })
      });

      const result = await response.json();

      if (result.success) {
        updateConfig({
          refImagePath: result.file_path
        });
        success('参考图像设置成功');
      } else {
        error('设置图像失败: ' + (result.error || '未知错误'));
      }
    } catch (err) {
      console.error('设置预设图像错误:', err);
      error('设置图像失败: ' + (err.message || '未知错误'));
    }
  };

  // 处理表单变更
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormState(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  // 处理表单提交
  const handleSubmit = (e) => {
    e.preventDefault();

    updateConfig(formState);

    // 向服务器发送配置
    if (isConnected) {
      sendMessage('config', { config: formState });
      success('设置已保存');
    } else {
      error('未连接到服务器，设置仅保存在本地');
    }

    onClose();
  };

  // 触发文件选择器
  const triggerFileInput = () => {
    fileInputRef.current?.click();
  };

  return (
    <>
      <div 
        className={`settings-overlay ${isOpen ? 'active' : ''}`}
        onClick={onClose}
      ></div>

      <div className={`settings-panel ${isOpen ? 'active' : ''}`}>
        <div className="settings-header">
          <h2>设置</h2>
          <button className="close-settings" onClick={onClose}>&times;</button>
        </div>

        <form className="settings-form" onSubmit={handleSubmit}>
          <div className="settings-section">
            <h3>语言与语音</h3>

            <div className="form-group">
              <label htmlFor="language-select">对话语言</label>
              <select
                id="language-select"
                name="language"
                value={formState.language}
                onChange={handleChange}
                className="settings-select"
              >
                <option value="zh-CN">中文 (简体)</option>
                <option value="en-US">英语 (美国)</option>
                <option value="ja-JP">日语</option>
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="voice-select">AI语音</label>
              <select
                id="voice-select"
                name="voice"
                value={formState.voice}
                onChange={handleChange}
                className="settings-select"
              >
                {voiceOptions.map(option => (
                  <option key={option.value} value={option.value}>
                    {option.label}
                  </option>
                ))}
              </select>
            </div>
          </div>

          <div className="settings-section">
            <h3>数字人设置</h3>

            <div className="reference-image-section">
              <h4>参考图像</h4>
              <p className="setting-description">选择或上传一张清晰的人物照片，作为视频生成的参考</p>

              <div className="gender-selector">
                <button 
                  type="button"
                  className={`gender-button ${selectedGender === 'woman' ? 'active' : ''}`}
                  onClick={() => setSelectedGender('woman')}
                >
                  女性图像
                </button>
                <button 
                  type="button"
                  className={`gender-button ${selectedGender === 'man' ? 'active' : ''}`}
                  onClick={() => setSelectedGender('man')}
                >
                  男性图像
                </button>
              </div>

              <div className="preset-images-grid">
                {presetImages.map((image, index) => (
                  <div 
                    key={index} 
                    className={`preset-image-item ${formState.refImagePath === image.path ? 'selected' : ''}`}
                    onClick={() => handlePresetImageSelect(image.path)}
                  >
                    <img src={image.thumbnail} alt={`预设图像 ${index+1}`} />
                  </div>
                ))}

                {presetImages.length === 0 && (
                  <p className="no-presets">加载预设图像中...</p>
                )}
              </div>

              <div className="image-upload-container">
                {formState.refImagePath ? (
                  <div className="image-preview">
                    <img 
                      src={formState.refImagePath} 
                      alt="参考图像" 
                      className="reference-image-preview"
                    />
                    <button 
                      type="button"
                      className="change-image-button"
                      onClick={triggerFileInput}
                    >
                      更换图像
                    </button>
                  </div>
                ) : (
                  <button 
                    type="button"
                    className="upload-button"
                    onClick={triggerFileInput}
                    disabled={isUploading}
                  >
                    {isUploading ? '上传中...' : '上传自定义图像'}
                  </button>
                )}

                <input 
                  ref={fileInputRef}
                  type="file" 
                  accept="image/jpeg,image/png,image/webp"
                  onChange={handleRefImageUpload}
                  style={{ display: 'none' }}
                />
              </div>
            </div>
          </div>

          <div className="settings-section">
            <h3>界面设置</h3>

            <div className="form-group">
              <label className="toggle-label">
                <span>深色模式</span>
                <div className="toggle-switch">
                  <input
                    type="checkbox"
                    id="theme-toggle"
                    name="theme"
                    checked={formState.theme === 'dark'}
                    onChange={(e) => {
                      const newTheme = e.target.checked ? 'dark' : 'light';
                      setFormState(prev => ({
                        ...prev,
                        theme: newTheme
                      }));
                    }}
                  />
                  <span className="toggle-slider"></span>
                </div>
              </label>
            </div>
          </div>

          <div className="settings-footer">
            <button type="submit" className="save-settings">
              保存设置
            </button>
          </div>
        </form>
      </div>
    </>
  );
};

export default SettingsPanel;
