import React, { useEffect, useState } from 'react';
import './App.css';
import Header from './components/Header';
import DigitalHuman from './components/DigitalHuman';
import ChatSection from './components/ChatSection';
import NotificationContainer from './components/NotificationContainer';
import SettingsPanel from './components/SettingsPanel';
import LoadingOverlay from './components/LoadingOverlay';
import StatusBar from './components/StatusBar';
import EchoMimicPlayer from './components/EchoMimicPlayer';
import { useConfig } from './contexts/ConfigContext';
import { useWebSocket } from './contexts/WebSocketContext';

function App() {
  const { config } = useConfig();
  const { connectionStatus, statusMessage, isLoading, loadingMessage } = useWebSocket();
  const [settingsOpen, setSettingsOpen] = useState(false);
  const [videoSrc, setVideoSrc] = useState(null);
  const [showEchoMimic, setShowEchoMimic] = useState(false);
  const [debugMode, setDebugMode] = useState(true);

  // 处理主题切换
  useEffect(() => {
    const htmlElement = document.documentElement;

    if (config.theme === 'dark') {
      htmlElement.classList.add('dark-theme');
    } else {
      htmlElement.classList.remove('dark-theme');
    }
  }, [config.theme]);

  // 监听WebSocket消息以获取生成的视频
  useEffect(() => {
    // 创建事件监听器
    const handleVideoAvailable = (event) => {
      const videoData = event.detail;
      setVideoSrc(videoData.videoUrl);
      setShowEchoMimic(true);
    };

    // 添加事件监听器
    window.addEventListener('videoAvailable', handleVideoAvailable);

    // 添加调试信息
    console.log('App组件已加载', {
      config,
      connectionStatus,
      statusMessage
    });

    // 清理函数
    return () => {
      window.removeEventListener('videoAvailable', handleVideoAvailable);
    };
  }, [config, connectionStatus, statusMessage]);

  // 切换设置面板
  const toggleSettings = () => {
    setSettingsOpen(!settingsOpen);
  };

  // 获取状态栏显示的状态
  const getStatusBarState = () => {
    switch (connectionStatus) {
      case 'online':
        return 'online';
      case 'connecting':
        return 'connecting';
      case 'offline':
      default:
        return 'offline';
    }
  };

  // 切换调试模式
  const toggleDebugMode = () => {
    setDebugMode(!debugMode);
  };

  return (
    <div className="container">
      <Header toggleSettings={toggleSettings} />

      <div style={{ position: 'fixed', top: '10px', right: '10px', zIndex: 1000 }}>
        <button onClick={toggleDebugMode} style={{ background: '#333', color: 'white', padding: '5px 10px' }}>
          {debugMode ? '关闭调试' : '开启调试'}
        </button>
      </div>

      {debugMode && (
        <div style={{ padding: '10px', background: '#f0f0f0', border: '1px solid #ccc', margin: '10px 0' }}>
          <h3>调试信息</h3>
          <p>连接状态: {connectionStatus}</p>
          <p>状态消息: {statusMessage}</p>
          <p>加载状态: {isLoading ? '加载中' : '未加载'}</p>
          <p>加载消息: {loadingMessage}</p>
          <p>使用EchoMimic: {config.useEchoMimic ? '是' : '否'}</p>
          <p>主题: {config.theme}</p>
          <p>显示EchoMimic播放器: {showEchoMimic ? '是' : '否'}</p>
          <p>视频源: {videoSrc || '无'}</p>
          <p>WebSocket端点: ws://localhost:8000/ws</p>
        </div>
      )}

      <main className="main-content">
        <div className="digital-human-container">
          {config.useEchoMimic ? (
            showEchoMimic ? (
              <EchoMimicPlayer 
                videoSrc={videoSrc} 
                onClose={() => setShowEchoMimic(false)}
              />
            ) : (
              <div className="waiting-container">
                <p>等待视频生成...</p>
              </div>
            )
          ) : (
            <DigitalHuman modelPath={config.modelPath} />
          )}
        </div>

        <div className="chat-container">
          <ChatSection />
        </div>
      </main>

      <StatusBar 
        status={getStatusBarState()} 
        message={statusMessage} 
      />

      <SettingsPanel 
        isOpen={settingsOpen} 
        onClose={() => setSettingsOpen(false)} 
      />

      <NotificationContainer />

      {isLoading && (
        <LoadingOverlay message={loadingMessage} />
      )}
    </div>
  );
}

export default App;
