import React, { useState, useEffect, useRef } from 'react';
import './SettingsPanel.css';
import { useConfig } from '../../contexts/ConfigContext';
import { useWebSocket } from '../../contexts/WebSocketContext';
import { useNotification } from '../../contexts/NotificationContext';

const SettingsPanel = ({ isOpen, onClose }) => {
  const { config, updateConfig } = useConfig();
  const { sendMessage, isConnected } = useWebSocket();
  const { success, error } = useNotification();

  // 本地表单状态
  const [formState, setFormState] = useState(config);
  const [voiceOptions, setVoiceOptions] = useState([]);
  const [uploadedImage, setUploadedImage] = useState(null);
  const [isUploading, setIsUploading] = useState(false);
  const fileInputRef = useRef(null);

  // 当配置变化时更新表单状态
  useEffect(() => {
    setFormState(config);
  }, [config]);

  // 根据语言更新语音选项
  useEffect(() => {
    updateVoiceOptions(formState.language);
  }, [formState.language]);

  // 处理表单变更
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormState(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  // 处理图像上传
  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    // 验证文件类型
    const validTypes = ['image/jpeg', 'image/png', 'image/webp'];
    if (!validTypes.includes(file.type)) {
      error('只支持 JPG, PNG 或 WebP 格式的图像');
      return;
    }

    // 验证文件大小
    if (file.size > 5 * 1024 * 1024) { // 5MB
      error('图像大小不能超过 5MB');
      return;
    }

    try {
      setIsUploading(true);

      // 创建FormData
      const formData = new FormData();
      formData.append('file', file);

      // 上传图像
      const response = await fetch('/api/upload_reference_image', {
        method: 'POST',
        body: formData
      });

      const result = await response.json();

      if (result.success) {
        setUploadedImage(result.imagePath);
        setFormState(prev => ({
          ...prev,
          refImagePath: result.imagePath
        }));
        success('参考图像上传成功');
      } else {
        throw new Error(result.error || '上传失败');
      }

    } catch (err) {
      console.error('上传错误:', err);
      error(`上传失败: ${err.message}`);
    } finally {
      setIsUploading(false);
    }
  };

  // 触发文件选择器
  const triggerFileInput = () => {
    fileInputRef.current?.click();
  };

  // 应用设置
  const handleSubmit = (e) => {
    e.preventDefault();

    updateConfig(formState);

    // 向服务器发送配置
    if (isConnected) {
      sendMessage('config', { config: formState });
      success('设置已保存');
    } else {
      error('未连接到服务器，设置仅保存在本地');
    }

    onClose();
  };

  // 根据语言更新语音选项
  const updateVoiceOptions = (language) => {
    let options = [];

    switch (language) {
      case 'zh-CN':
        options = [
          { value: 'aura-mandarin', text: 'Aura (女声)' },
          { value: 'nova-mandarin', text: 'Nova (女声)' },
          { value: 'shimmer-mandarin', text: 'Shimmer (女声)' },
          { value: 'storm-mandarin', text: 'Storm (男声)' },
          { value: 'warp-mandarin', text: 'Warp (男声)' }
        ];
        break;

      case 'en-US':
        options = [
          { value: 'aura', text: 'Aura (女声)' },
          { value: 'nova', text: 'Nova (女声)' },
          { value: 'shimmer', text: 'Shimmer (女声)' },
          { value: 'storm', text: 'Storm (男声)' },
          { value: 'warp', text: 'Warp (男声)' }
        ];
        break;

      case 'ja-JP':
        options = [
          { value: 'aura-japanese', text: 'Aura (女声)' },
          { value: 'nova-japanese', text: 'Nova (女声)' },
          { value: 'storm-japanese', text: 'Storm (男声)' }
        ];
        break;

      default:
        options = [
          { value: 'aura', text: 'Aura (默认)' }
        ];
    }

    setVoiceOptions(options);

    // 如果当前选择的语音在新的选项中不存在，则设置为第一个选项
    const voiceExists = options.some(option => option.value === formState.voice);
    if (!voiceExists && options.length > 0) {
      setFormState(prev => ({
        ...prev,
        voice: options[0].value
      }));
    }
  };

  return (
    <>
      <div 
        className={`settings-overlay ${isOpen ? 'active' : ''}`}
        onClick={onClose}
      ></div>

      <div className={`settings-panel ${isOpen ? 'active' : ''}`}>
        <div className="settings-header">
          <h2>设置</h2>
          <button className="close-settings" onClick={onClose}>&times;</button>
        </div>

        <form className="settings-form" onSubmit={handleSubmit}>
          <div className="settings-section">
            <h3>语言与语音</h3>

            <div className="form-group">
              <label htmlFor="language-select">对话语言</label>
              <select
                id="language-select"
                name="language"
                value={formState.language}
                onChange={handleChange}
                className="settings-select"
              >
                <option value="zh-CN">中文 (简体)</option>
                <option value="en-US">英语 (美国)</option>
                <option value="ja-JP">日语</option>
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="voice-select">AI语音</label>
              <select
                id="voice-select"
                name="voice"
                value={formState.voice}
                onChange={handleChange}
                className="settings-select"
              >
                {voiceOptions.map(option => (
                  <option key={option.value} value={option.value}>
                    {option.text}
                  </option>
                ))}
              </select>
            </div>
          </div>

          <div className="settings-section">
            <h3>Live2D 模型</h3>

            <div className="form-group">
              <label htmlFor="model-select">选择模型</label>
              <select
                id="model-select"
                name="modelPath"
                value={formState.modelPath}
                onChange={handleChange}
                className="settings-select"
              >
                <option value="/models/default/Hiyori/Hiyori.model3.json">Hiyori (女生)</option>
                <option value="/models/default/Chitose/Chitose.model3.json">Chitose (女生)</option>
                <option value="/models/default/Epsilon/Epsilon.model3.json">Epsilon (机器人)</option>
                <option value="/models/default/Haru-1/Haru-1.model3.json">Haru-1 (女生)</option>
                <option value="/models/default/Haru-2/Haru-2.model3.json">Haru-2 (女生)</option>
                <option value="/models/default/Hibiki/Hibiki.model3.json">Hibiki (女生)</option>
                <option value="/models/default/Izumi/Izumi.model3.json">Izumi (女生)</option>
                <option value="/models/default/Kei/Kei.model3.json">Kei (男生)</option>
                <option value="/models/default/Mao/Mao.model3.json">Mao (女生)</option>
                <option value="/models/default/Miara/Miara.model3.json">Miara (女生)</option>
                <option value="/models/default/Rice/Rice.model3.json">Rice (女生)</option>
                <option value="/models/default/Shizuku/Shizuku.model3.json">Shizuku (女生)</option>
                <option value="/models/default/Tsumiki/Tsumiki.model3.json">Tsumiki (女生)</option>
              </select>
            </div>

            <div className="form-group model-preview">
              <p className="model-info">当前选择的模型: {formState.modelPath.split('/').slice(-2)[0]}</p>
              <p className="model-path-info">模型路径: {formState.modelPath}</p>
            </div>
          </div>

          <div className="settings-section">
            <h3>EchoMimic 视频生成</h3>

            <div className="form-group">
              <label className="toggle-label">
                <span>启用 EchoMimic 视频生成</span>
                <div className="toggle-switch">
                  <input
                    type="checkbox"
                    id="echomimic-toggle"
                    name="useEchoMimic"
                    checked={formState.useEchoMimic}
                    onChange={handleChange}
                  />
                  <span className="toggle-slider"></span>
                </div>
              </label>
              <p className="setting-description">启用后将使用AI生成逼真的对话视频</p>
            </div>

            {formState.useEchoMimic && (
              <div className="reference-image-section">
                <h4>参考图像</h4>
                <p className="setting-description">上传一张清晰的人物照片，作为视频生成的参考</p>

                <div className="image-upload-container">
                  {uploadedImage || formState.refImagePath ? (
                    <div className="image-preview">
                      <img 
                        src={uploadedImage || formState.refImagePath} 
                        alt="参考图像" 
                        className="reference-image-preview"
                      />
                      <button 
                        type="button" 
                        className="change-image-btn"
                        onClick={triggerFileInput}
                      >
                        更换图像
                      </button>
                    </div>
                  ) : (
                    <button 
                      type="button" 
                      className="upload-image-btn"
                      onClick={triggerFileInput}
                      disabled={isUploading}
                    >
                      {isUploading ? '正在上传...' : '上传参考图像'}
                    </button>
                  )}
                  <input 
                    ref={fileInputRef}
                    type="file" 
                    accept="image/jpeg,image/png,image/webp"
                    onChange={handleImageUpload}
                    style={{ display: 'none' }}
                  />
                </div>

                <div className="form-group">
                  <p className="image-upload-tip">提示: 上传人物正面照，肩膀以上部分，清晰光线好的照片效果最佳</p>
                </div>
              </div>
            )}
          </div>

          <div className="settings-section">
            <h3>界面设置</h3>

            <div className="form-group">
              <label className="toggle-label">
                <span>深色模式</span>
                <div className="toggle-switch">
                  <input
                    type="checkbox"
                    id="theme-toggle"
                    name="theme"
                    checked={formState.theme === 'dark'}
                    onChange={(e) => {
                      const newTheme = e.target.checked ? 'dark' : 'light';
                      setFormState(prev => ({
                        ...prev,
                        theme: newTheme
                      }));
                    }}
                  />
                  <span className="toggle-slider"></span>
                </div>
              </label>
            </div>
          </div>

          <div className="settings-footer">
            <button type="submit" className="save-settings">
              保存设置
            </button>
          </div>
        </form>
      </div>
    </>
  );
};

export default SettingsPanel;
