import React, { createContext, useState, useContext, useEffect, useCallback } from 'react';
import { useConfig } from './ConfigContext';
import { useNotification } from './NotificationContext';

// 创建上下文
const WebSocketContext = createContext();

// WebSocket提供者组件
export const WebSocketProvider = ({ children }) => {
  const { config } = useConfig();
  const { success, error } = useNotification();

  const [socket, setSocket] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('offline');
  const [statusMessage, setStatusMessage] = useState('等待连接...');
  const [isLoading, setIsLoading] = useState(false);
  const [loadingMessage, setLoadingMessage] = useState('');
  const [lastMessage, setLastMessage] = useState(null);
  const [messages, setMessages] = useState([]);
  const [isProcessing, setIsProcessing] = useState(false);

  // 初始化WebSocket连接
  const initWebSocket = useCallback(() => {
    // 修改WebSocket URL，确保连接到正确的后端地址
    const wsUrl = 'ws://localhost:8000/ws';

    let reconnectAttempts = 0;
    const maxReconnectAttempts = 5;
    const baseReconnectDelay = 1000; // 1秒

    const connect = () => {
      updateStatus('connecting', `连接中... ${reconnectAttempts > 0 ? `(尝试 ${reconnectAttempts}/${maxReconnectAttempts})` : ''}`);

      console.log(`尝试连接到WebSocket: ${wsUrl}`);

      // 创建WebSocket连接
      const newSocket = new WebSocket(wsUrl);

      // 连接打开
      newSocket.onopen = () => {
        console.log('WebSocket连接已建立');
        setIsConnected(true);
        updateStatus('online', '已连接');
        reconnectAttempts = 0; // 重置重连计数

        // 发送配置
        newSocket.send(JSON.stringify({
          type: 'config',
          config: config
        }));

        // 显示成功通知
        if (reconnectAttempts > 0) {
          success('连接已恢复');
        }
      };

      // 连接关闭
      newSocket.onclose = (event) => {
        console.log('WebSocket连接已关闭:', event);
        setIsConnected(false);

        if (reconnectAttempts < maxReconnectAttempts) {
          // 指数退避重连
          const delay = Math.min(baseReconnectDelay * Math.pow(2, reconnectAttempts), 30000);
          reconnectAttempts++;

          updateStatus('reconnecting', `连接已断开，${delay / 1000}秒后重连... (${reconnectAttempts}/${maxReconnectAttempts})`);

          // 显示重连通知
          if (reconnectAttempts === 1) {
            error('连接已断开，正在尝试重连...');
          }

          setTimeout(connect, delay);
        } else {
          updateStatus('offline', '连接失败，请检查网络并刷新页面');
          error('连接失败，请检查网络并刷新页面');
        }
      };

      // 连接错误
      newSocket.onerror = (err) => {
        console.error('WebSocket连接错误:', err);
        error('连接发生错误，正在尝试重连...');
      };

      // 收到消息
      newSocket.onmessage = (event) => {
        try {
          // 检查是否为二进制数据（音频）
          if (event.data instanceof Blob) {
            console.log('收到二进制数据:', event.data.size, event.data.type);
            handleAudioMessage(event.data);
            return;
          }

          // 尝试解析JSON消息
          const data = JSON.parse(event.data);
          console.log('收到JSON消息:', data);

          // 更新最近消息状态
          setLastMessage(data);

          // 根据消息类型处理
          switch (data.type) {
            case 'thinking':
              setLoading(true, data.message || '思考中...');
              break;
            case 'bot_reply':
              setLoading(false);
              break;
            case 'generating_video':
              setLoading(true, data.message || '生成视频中...');
              break;
            case 'video_ready':
              setLoading(false);
              if (data.success && data.video_url) {
                console.log('视频已就绪:', data.video_url);
              }
              break;
            case 'error':
              setLoading(false);
              error(data.message || '发生错误');
              break;
            default:
              break;
          }
        } catch (err) {
          console.error('处理WebSocket消息错误:', err);
        }
      };

      setSocket(newSocket);
    };

    connect();

    // 组件卸载时清理WebSocket连接
    return () => {
      if (socket) {
        socket.close();
      }
    };
  }, [config, success, error]);

  // 组件挂载时初始化WebSocket
  useEffect(() => {
    initWebSocket();

    // 清理函数
    return () => {
      if (socket) {
        socket.close();
      }
    };
  }, [initWebSocket]);

  // 更新状态
  const updateStatus = (status, message) => {
    setConnectionStatus(status);
    setStatusMessage(message);
  };

  // 设置加载状态
  const setLoading = (isLoading, message = '') => {
    setIsLoading(isLoading);
    setLoadingMessage(message);
  };

  // 发送文本消息
  const sendMessage = useCallback((text) => {
    if (!socket || socket.readyState !== WebSocket.OPEN) {
      error('WebSocket连接未建立，无法发送消息');
      return;
    }

    try {
      // 添加用户消息到消息列表
      setMessages(prev => [...prev, { role: 'user', content: text }]);

      // 设置处理状态
      setIsProcessing(true);

      // 发送消息到服务器
      socket.send(JSON.stringify({
        type: 'text_input',
        text: text
      }));
    } catch (err) {
      error(`发送消息失败: ${err.message}`);
      setIsProcessing(false);
    }
  }, [socket, error]);

  // 发送音频数据
  const sendAudioData = useCallback(async (audioBlob) => {
    if (!socket || socket.readyState !== WebSocket.OPEN) {
      error('WebSocket连接未打开，无法发送音频');
      return;
    }

    try {
      // 验证音频Blob的有效性
      if (!audioBlob || audioBlob.size === 0) {
        error('无效的音频数据，无法发送');
        return;
      }

      const mimeType = audioBlob.type || 'audio/webm';
      console.log(`准备发送音频数据，大小: ${audioBlob.size} 字节, 类型: ${mimeType}`);

      // 设置处理状态
      setIsProcessing(true);

      // 先发送一个描述音频格式的JSON消息
      socket.send(JSON.stringify({
        type: 'audio_input',
        format: mimeType,
        size: audioBlob.size,
        timestamp: new Date().toISOString()
      }));

      // 等待一小段时间确保消息已处理
      await new Promise(resolve => setTimeout(resolve, 50));

      // 然后发送二进制音频数据
      console.log('开始发送音频数据...');
      socket.send(audioBlob);

      console.log('音频数据已发送到服务器');
    } catch (err) {
      console.error('发送音频失败:', err);
      error(`发送音频失败: ${err.message}`);
      setIsProcessing(false);
    }
  }, [socket, error]);

  // 发送音频数据
  const sendAudio = useCallback((audioBlob) => {
    sendAudioData(audioBlob);
  }, [sendAudioData]);

  // 处理服务器消息
  const handleServerMessage = (message) => {
    try {
      const data = JSON.parse(message.data);
      console.log('收到服务器消息:', data);

      if (data.type === 'video_ready') {
        // 处理视频生成完成消息
        setLastMessage(data);
        setIsProcessing(false);
      } else if (data.type === 'text_response') {
        // 处理文本响应
        setMessages(prev => [...prev, { role: 'assistant', content: data.text }]);
        setIsProcessing(false);
      } else if (data.type === 'error') {
        // 处理错误消息
        error(data.message || '服务器返回错误');
        setIsProcessing(false);
      } else if (data.type === 'processing') {
        // 处理处理中状态
        setLoadingMessage(data.message || '处理中...');
      }
    } catch (err) {
      console.error('处理服务器消息失败:', err, message.data);
    }
  };

  // 处理音频响应
  const handleAudioResponse = (message) => {
    console.log('收到音频响应', message);

    if (message.audio) {
      try {
        // 将 base64 编码的音频解码为 ArrayBuffer
        const audioData = Uint8Array.from(atob(message.audio), c => c.charCodeAt(0)).buffer;

        // 创建 Blob
        const blob = new Blob([audioData], { type: 'audio/wav' });

        // 创建 URL 并播放
        const audioUrl = URL.createObjectURL(blob);
        const audio = new Audio(audioUrl);

        audio.onended = () => {
          URL.revokeObjectURL(audioUrl);
        };

        audio.play().catch(e => {
          console.error('播放音频失败:', e);
          error('播放音频失败');
        });
      } catch (err) {
        console.error('处理音频响应失败:', err);
        error('处理音频响应失败');
      }
    }
  };

  // 处理音频消息
  const handleAudioMessage = (audioData) => {
    console.log('收到音频消息', audioData);

    try {
      // 创建 Blob
      const blob = new Blob([audioData], { type: 'audio/wav' });

      // 创建 URL 并播放
      const audioUrl = URL.createObjectURL(blob);
      const audio = new Audio(audioUrl);

      audio.onended = () => {
        URL.revokeObjectURL(audioUrl);
      };

      audio.play().catch(e => {
        console.error('播放音频失败:', e);
        error('播放音频失败');
      });
    } catch (err) {
      console.error('处理音频消息失败:', err);
      error('处理音频消息失败');
    }
  };

  // 提供上下文值
  const contextValue = {
    socket,
    isConnected,
    connectionStatus,
    statusMessage,
    isLoading,
    loadingMessage,
    lastMessage,
    messages,
    isProcessing,
    sendMessage,
    sendAudio,
    setLoading
  };

  return (
    <WebSocketContext.Provider value={contextValue}>
      {children}
    </WebSocketContext.Provider>
  );
};

// 自定义Hook，用于在组件中访问WebSocket功能
export const useWebSocket = () => {
  const context = useContext(WebSocketContext);
  if (!context) {
    throw new Error('useWebSocket 必须在 WebSocketProvider 内部使用');
  }
  return context;
};
